func show(board [][]byte) {
	for i := 0; i < len(board); i++ {
		for j := 0; j < len(board[i]); j++ {
			fmt.Print(string(board[i][j]) + ", ")
		}
		fmt.Println()
	}
}

var direction = [][]int{
	{-1, 0},  //上
	{1, 0},   //下
	{0, -1},  //左
	{0, 1},   //右
	{-1, -1}, //左上
	{1, -1},  //左下
	{-1, 1},  //右上
	{1, 1},   //右下
}

//深度优先
func updateBoard(board [][]byte, click []int) [][]byte {
	m := len(board)
	if m == 0 {
		return board
	}
	n := len(board[0])
	if click[0] >= m || click[0] < 0 || click[1] >= n || click[1] < 0 {
		return board
	}

	if board[click[0]][click[1]] == 'M' {
		board[click[0]][click[1]] = 'X'
		return board
	}
	if board[click[0]][click[1]] != 'E' {
		return board
	}

	var f func(board [][]byte, row, col int)

	f = func(board [][]byte, row, col int) {
		var count int
		for _, v := range direction {
			newRow := row + v[0]
			newCol := col + v[1]
			if newRow < m && newRow >= 0 && newCol < n && newCol >= 0 && board[newRow][newCol] == 'M' {
				count++
			}
		}

		if count != 0 {
			board[row][col] = '0' + byte(count)
		} else {
			board[row][col] = 'B'
			for _, v := range direction {
				newRow, newCol := row+v[0], col+v[1]
				if newRow < m && newRow >= 0 && newCol < n && newCol >= 0 && board[newRow][newCol] == 'E' {
					f(board, newRow, newCol)
				}
			}
		}

	}

	f(board, click[0], click[1])

	return board
}

//广度优先
func updateBoard1(board [][]byte, click []int) [][]byte {
	m := len(board)
	if m == 0 {
		return board
	}
	n := len(board[0])
	if click[0] >= m || click[0] < 0 || click[1] >= n || click[1] < 0 {
		return board
	}

	if board[click[0]][click[1]] == 'M' {
		board[click[0]][click[1]] = 'X'
		return board
	}
	if board[click[0]][click[1]] != 'E' {
		return board
	}

	queue := make([][]int, 0)
	queue = append(queue, []int{click[0], click[1]})

	for len(queue) > 0 {
		this := queue[0]
		queue = queue[1:]
		var count int
		for _, v := range direction {
			newRow := this[0] + v[0]
			newCol := this[1] + v[1]
			if newRow < m && newRow >= 0 && newCol < n && newCol >= 0 && board[newRow][newCol] == 'M' {
				count++
			}
		}
		if count > 0 {
			board[this[0]][this[1]] = '0' + byte(count)
		} else {
			board[this[0]][this[1]] = 'B'
			for _, v := range direction {
				newRow := this[0] + v[0]
				newCol := this[1] + v[1]
				if newRow < m && newRow >= 0 && newCol < n && newCol >= 0 && board[newRow][newCol] == 'E' {
					if !find(queue, []int{newRow, newCol}) {
						queue = append(queue, []int{newRow, newCol})
					}
				}
			}
		}
	}
	return board
}

func find(queue [][]int, this []int) bool {
	for _, v := range queue {
		if this[0] == v[0] && this[1] == v[1] {
			return true
		}
	}
	return false
}

var dirX = []int{0, 1, 0, -1, 1, 1, -1, -1}
var dirY = []int{1, 0, -1, 0, 1, -1, 1, -1}

// 官方解法，深度优先
func updateBoardOfficial1(board [][]byte, click []int) [][]byte {
	x, y := click[0], click[1]
	if board[x][y] == 'M' {
		board[x][y] = 'X'
	} else {
		dfs(board, x, y)
	}
	return board
}

func dfs(board [][]byte, x, y int) {
	cnt := 0
	for i := 0; i < 8; i++ {
		tx, ty := x+dirX[i], y+dirY[i]
		if tx < 0 || tx >= len(board) || ty < 0 || ty >= len(board[0]) {
			continue
		}
		// 不用判断 M，因为如果有 M 的话游戏已经结束了
		if board[tx][ty] == 'M' {
			cnt++
		}
	}
	if cnt > 0 {
		board[x][y] = byte(cnt + '0')
	} else {
		board[x][y] = 'B'
		for i := 0; i < 8; i++ {
			tx, ty := x+dirX[i], y+dirY[i]
			// 这里不需要在存在 B 的时候继续扩展，因为 B 之前被点击的时候已经被扩展过了
			if tx < 0 || tx >= len(board) || ty < 0 || ty >= len(board[0]) || board[tx][ty] != 'E' {
				continue
			}
			dfs(board, tx, ty)
		}
	}
}

// 官方解法，广度优先
func updateBoardOfficial2(board [][]byte, click []int) [][]byte {
	x, y := click[0], click[1]
	if board[x][y] == 'M' {
		board[x][y] = 'X'
	} else {
		bfs(board, x, y)
	}
	return board
}

func bfs(board [][]byte, sx, sy int) {
	queue := [][]int{}
	vis := make([][]bool, len(board))
	for i := 0; i < len(vis); i++ {
		vis[i] = make([]bool, len(board[0]))
	}
	queue = append(queue, []int{sx, sy})
	vis[sx][sy] = true
	for i := 0; i < len(queue); i++ {
		cnt, x, y := 0, queue[i][0], queue[i][1]
		for i := 0; i < 8; i++ {
			tx, ty := x+dirX[i], y+dirY[i]
			if tx < 0 || tx >= len(board) || ty < 0 || ty >= len(board[0]) {
				continue
			}
			// 不用判断 M，因为如果有 M 的话游戏已经结束了
			if board[tx][ty] == 'M' {
				cnt++
			}
		}
		if cnt > 0 {
			board[x][y] = byte(cnt + '0')
		} else {
			board[x][y] = 'B'
			for i := 0; i < 8; i++ {
				tx, ty := x+dirX[i], y+dirY[i]
				// 这里不需要在存在 B 的时候继续扩展，因为 B 之前被点击的时候已经被扩展过了
				if tx < 0 || tx >= len(board) || ty < 0 || ty >= len(board[0]) || board[tx][ty] != 'E' || vis[tx][ty] {
					continue
				}
				queue = append(queue, []int{tx, ty})
				vis[tx][ty] = true
			}
		}
	}
}
