//我自己写的贪心法，有问题，最长的过不了
func isPossible(nums []int) bool {
	n := len(nums)
	if n < 3 {
		return false
	}
	length := nums[n-1] - nums[0] + 1
	if length < 3 {
		return false
	}
	arr := make([]int, length)
	for _, num := range nums {
		arr[num-nums[0]]++
	}

	matrix := []int{}
	start := 0
	for n > 0 {
		for i := start; i < length; i++ {
			if arr[i] == 0 {
				if len(matrix) != 0 {
					break
				}
				start++
				continue
			}
			matrix = append(matrix, i)
			arr[i]--
			n--
			if arr[i] == 0 {
				start = i + 1
				continue
			}
			if i+1 < length && arr[i+1] != 1 {
				continue
			}
			break
		}
		fmt.Println(matrix)
		if len(matrix) > 2 {
			matrix = []int{}
			continue
		} else {
			return false
		}
	}

	return true
}

type hp struct{ sort.IntSlice }

func (h *hp) Push(v interface{}) { h.IntSlice = append(h.IntSlice, v.(int)) }
func (h *hp) Pop() interface{} {
	a := h.IntSlice
	v := a[len(a)-1]
	h.IntSlice = a[:len(a)-1]
	return v
}
func (h *hp) push(v int) { heap.Push(h, v) }
func (h *hp) pop() int   { return heap.Pop(h).(int) }

func isPossibleOfficial(nums []int) bool {
	lens := map[int]*hp{}
	for _, v := range nums {
		if lens[v] == nil {
			lens[v] = new(hp)
		}
		if h := lens[v-1]; h != nil {
			prevLen := h.pop()
			if h.Len() == 0 {
				delete(lens, v-1)
			}
			lens[v].push(prevLen + 1)
		} else {
			lens[v].push(1)
		}
	}

	for _, h := range lens {
		if h.IntSlice[0] < 3 {
			return false
		}
	}
	return true
}
